        FirstName.setText(null);
        LastName.setText(null);
        ID.setText(null);
        Age.setText(null);

        Gender.setSelectedIndex(0);
        Day.setSelectedIndex(0);
        Month.setSelectedIndex(0);
        Year.setSelectedIndex(0);

