    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        //varible
        int listOfGender = Gender.getSelectedIndex();
        char gender = (listOfGender == 0 ? 'M' : 'F');

        int dayComboBox = (Day.getSelectedIndex() + 1);
        int monthComboBox = (Month.getSelectedIndex() + 1);
        int yearComboBox= (Year.getSelectedIndex() + 1900);
        
        

        String firstNameTextField = "";
        String lastNameTextField = "";

        int idTextField;
        int ageTextField;

        //New Object to save the value
        Patient patient;
        //Exception Handing
        do {

            firstNameTextField = FirstName.getText();

            if (firstNameTextField.isEmpty()) {
                FirstName.setForeground(Color.red);
                FirstName.setText("No Input!!");
                break;
            }

            char firstNameTextFieldToChar[] = firstNameTextField.toCharArray();

            for (int i = 0; i < firstNameTextFieldToChar.length; i++) {
                if (Character.isDigit(firstNameTextFieldToChar[i])) {
                    FirstName.setForeground(Color.red);
                    FirstName.setText("Invalid Input!!");
                    break;
                }
            }

            lastNameTextField = LastName.getText();

            if (lastNameTextField.isEmpty()) {
                LastName.setForeground(Color.red);
                LastName.setText("No Input!!");
                break;
            }
            char lastNameToChar[] = lastNameTextField.toCharArray();

            for (int i = 0; i < lastNameToChar.length; i++) {
                if (Character.isDigit(lastNameToChar[i])) {
                    LastName.setForeground(Color.red);
                    LastName.setText("Invalid Input!!");
                    break;
                }
            }
            if (ID.getText().isEmpty()) {
                ID.setForeground(Color.red);
                ID.setText("No Input!!");
                break;
            } else {

                try {
                    idTextField = Integer.parseInt(ID.getText());

                } catch (NumberFormatException e) {
                    ID.setForeground(Color.red);
                    ID.setText("Invalid Input!!");
                    break;
                }
            }
            if (Age.getText().isEmpty()) {
                Age.setForeground(Color.red);
                Age.setText("No Input!!");
                break;
            } else {
                try {
                    ageTextField = Integer.parseInt(Age.getText());

                } catch (NumberFormatException e) {
                    Age.setForeground(Color.red);
                    Age.setText("Invalid Input!!");
                    break;
                }
            }
            
            patient = new Patient(null, firstNameTextField, lastNameTextField, idTextField, ageTextField, new Date(0, 0, dayComboBox, monthComboBox, yearComboBox), gender);
                 
             //Close the window
            dispose(); 
            Main.main(null);
        } while (true);
    }//GEN-LAST:event_jButton2ActionPerformed

